    public static ArrayList<CountryModel> getListData(){
        ArrayList<CountryModel> list = new ArrayList<>();
        for (String[] footballData : dataCountry){
            CountryModel countryModel = new CountryModel();

            countryModel.setNameaCountry(footballData[0]);
            countryModel.setDescCountry(footballData[1]);
            countryModel.setImageCountry(footballData[2]);
            countryModel.setImageFlagCountry(footballData[3]);
            countryModel.setImageIconCountry(footballData[4]);


            list.add(countryModel);

        }

        return list;

    }

